function ListGroup() {
    let myList = ['New York', 'Paris', 'London', 'Tokyo', 'Jaipur', 'Sydney'];
    let selectedIndex = -1;

    const handleClick = (event) => {selectedIndex};

    return (
        <>
            <h1>My List.</h1>
            {myList.length === 0 && <p>No element found.</p>}
            <ul className="list-group">
                {
                    myList.map((item, index) => (<li key={item} className={selectedIndex === index ? "list-group-item active" : "list-group-item"} onClick={handleClick}>{item}</li>))
                }
            </ul>
        </>
    )
}

export default ListGroup;
